import { getContext, setContext } from "svelte";

const PLAN_CONTEXT_KEY = Symbol("plan-context");

export type PlanContextValue = {
	isStreaming: boolean;
};

export function setPlanContext(value: PlanContextValue) {
	setContext(PLAN_CONTEXT_KEY, value);
}

export function getPlanContext(): PlanContextValue {
	const context = getContext<PlanContextValue>(PLAN_CONTEXT_KEY);
	if (!context) {
		throw new Error("Plan components must be used within Plan");
	}
	return context;
}

import type { HTMLAttributes } from "svelte/elements";
import type { Snippet } from "svelte";
import type { Collapsible as CollapsiblePrimitive } from "bits-ui";

export type PlanProps = CollapsiblePrimitive.RootProps & {
	isStreaming?: boolean;
	class?: string;
	children?: Snippet;
};

export type PlanHeaderProps = HTMLAttributes<HTMLDivElement> & {
	children?: Snippet;
};

export type PlanTitleProps = Omit<HTMLAttributes<HTMLHeadingElement>, "children"> & {
	children: Snippet;
};

export type PlanDescriptionProps = Omit<HTMLAttributes<HTMLParagraphElement>, "children"> & {
	children: Snippet;
};

export type PlanActionProps = HTMLAttributes<HTMLDivElement> & {
	children?: Snippet;
};

export type PlanContentProps = HTMLAttributes<HTMLDivElement> & {
	children?: Snippet;
};

export type PlanFooterProps = HTMLAttributes<HTMLDivElement> & {
	children?: Snippet;
};

export type PlanTriggerProps = CollapsiblePrimitive.TriggerProps & {
	class?: string;
	children?: Snippet;
};

export { default as Plan } from "./Plan.svelte";
export { default as PlanHeader } from "./PlanHeader.svelte";
export { default as PlanTitle } from "./PlanTitle.svelte";
export { default as PlanDescription } from "./PlanDescription.svelte";
export { default as PlanAction } from "./PlanAction.svelte";
export { default as PlanContent } from "./PlanContent.svelte";
export { default as PlanFooter } from "./PlanFooter.svelte";
export { default as PlanTrigger } from "./PlanTrigger.svelte";

export type {
	PlanProps,
	PlanHeaderProps,
	PlanTitleProps,
	PlanDescriptionProps,
	PlanActionProps,
	PlanContentProps,
	PlanFooterProps,
	PlanTriggerProps,
} from "./types.js";
